mapper.update:
UPDATE APPROVAL 
SET STATUS = 'N'
WHERE AP_NO = #{apNo}

UPDATE DAYOFF_FORM 
SET DO_TITLE = #{doTitle}
    , DO_START_DATE = #{doStartDate}
    , DO_END_DATE = #{doEndDate}
    , DO_CONTENT = #{doContent}
    , DO_TYPE = #{doType}
WHERE AP_NO = #{apNo}

UPDATE PROPOSAL_FORM 
SET PR_TITLE = #{prTitle}
    , PR_GOAL = #{prGoal}
    , PR_PLAN = #{prPlan}
    , PR_START_DATE = #{prStartDate}
    , PR_END_DATE = #{prEndDate}
    , PR_PERSON = #{prPerson}
    , PR_AMOUNT = #{prAmount}
WHERE AP_NO = #{apNo}

UPDATE PAYMENT_FORM 
SET PAY_TITLE = #{payTitle}
    , PAY_DAY = #{payDay}
    , PAY_LIST = #{payList}
    , PAY_AMOUNT = #{payAmount}
WHERE AP_NO = #{apNo}

UPDATE FORM_ATT 
SET ORIGIN_NAME = #{originName}
    , CHANGE_NAME = #{changeName}
WHERE AP_NO = #{apNo}
