mapper.select:
SELECT COUNT(*)
FROM MEETING_ROOM
WHERE ROOM_NO=#{roomNo}

SELECT COUNT(*)
FROM MEETING_ROOM
WHERE C_NO = #{userCNo}

SELECT ROOM_NO
FROM MEETING_ROOM
WHERE ROOM_NAME = #{selectRoom}

SELECT ROOM_NO, START_TIME, END_TIME
FROM RESERVE_ROOM
WHERE ROOM_NO = #{roomNo}
AND RESERVE_DATE = #{reserveDate}

SELECT ROOM_NO, START_TIME, END_TIME
FROM RESERVE_ROOM
WHERE C_NO = #{cNo}
AND RESERVE_DATE = #{reserveDate}

SELECT *
FROM RESERVE_ROOM
WHERE RESERVE_DATE = #{reserveDate}
AND START_TIME = #{startTime}
AND ROOM_NO = #{roomNo}

SELECT M_NAME
FROM MEMBER
WHERE M_NO = #{mNo}

SELECT ROOM_NAME
FROM MEETING_ROOM
WHERE ROOM_NO = #{roomNo}

SELECT RESERVE_DATE
FROM RESERVE_ROOM
WHERE RESERVE_NO = #{reservationNo}

mapper.update:
UPDATE RESERVE_ROOM
SET ROOM_NO = #{roomNo}, RESERVE_DATE = #{reserveDate}, 
	START_TIME = #{startTime}, END_TIME = #{endTime}, RESERVE_TITLE = #{reserveTitle}, 
	M_NO = #{mNo}, C_NO = #{cNo}
WHERE RESERVE_NO = #{reserveNo}
