mapper.update:
UPDATE MEMBER
SET M_PWDERROR = M_PWDERROR + 1
WHERE M_ID=#{mId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE C_NO = #{cNo}

mapper.update:
UPDATE MEMBER
SET M_EMAIL=#{mEmail}, M_PHONE=#{mPhone}, M_ADDRESS=#{mAddress}, M_ENT_DATE=#{mEntDate}, M_HIRE_DATE = #{mHireDate},
	 M_MANAGER=#{mManager}, D_NO=#{dNo}, J_NO=#{jNo}
WHERE M_NO=#{mNo}

UPDATE MEMBER
SET M_WORK = 'N', M_HIRE_DATE = SYSDATE
WHERE M_NO=#{mNo}

UPDATE MEMBER
SET M_EMAIL=#{mEmail}, M_PHONE=#{mPhone}, M_ADDRESS=#{mAddress}
WHERE M_NO=#{mNo}

UPDATE MEMBER
SET M_PWD=#{mPwd}
WHERE M_NO=#{mNo}

UPDATE MEMBER
SET M_PWD=#{mPwd}, M_PWDERROR = 0
WHERE M_NO=#{mNo}

UPDATE PHOTO
SET P_NAME=#{pName}
WHERE P_NO=#{pNo}

mapper.select:
SELECT *
FROM VIEW_LOGINUSER
WHERE M_ID=#{mId} AND M_EMAIL=#{mEmail}
