mapper.select:
select * from notice
where No_Status='Y' and C_No=#{companyNo}
order by DECODE( No_Important,'Y',1), No_Num desc

mapper.update:
update notice
set No_Status='N'
where No_Num=#{No_Num}

update notice
set No_Title=#{No_Title}, No_Content=#{No_Content}, No_Important=#{No_Important}
where No_Num=#{No_Num}
